import { useEffect, useState } from "react";
import { MDBRadio, MDBBtnGroup } from 'mdb-react-ui-kit';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import ResultCard from "./ResultCard";
import axios from "axios";
import { useToast, Button } from '@chakra-ui/react';
import {
    Stack,
    FormControl,
    Input,
    useColorModeValue,
    Heading,
    Container,
    Flex,
    Divider,
    Switch,
    CircularProgress,
    CircularProgressLabel
} from '@chakra-ui/react';
import { ArrowForwardIcon } from '@chakra-ui/icons';
import rock_img from "../images/stone.png"



/* function CircularProgressWithLabel(props) {
  return (
    <Box sx={{ position: 'relative', display: 'inline-flex' }}>
      <CircularProgress size={120} variant="determinate" {...props} />
      <Box
        sx={{
          top: 0,
          left: 0,
          bottom: 0,
          right: 0,
          position: 'absolute',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        {`${props.value/10}S`}
      </Box>
    </Box>
  );
} */

function GameRoom(props) {

    const [currentUser, setCurrentUser] = useState(props.currentUser);
    const [roomNumber, setRoomNumber] = useState(props.roomNumber);
    const [connections, setConnections] = useState(0);
    const [websocket, setWebsocket] = useState(null);
    const [myStatus, setMyStatus] = useState(false);
    const [opponentStatus, setOpponentStatus] = useState(false);
    const [gameStarted, isGameStarted] = useState(false);
    const [gameEnded, isGameEnded] = useState(false);
    const [myChoice, setMyChoice] = useState("rock");
    const [countDown, setCountDown] = useState(30);
    const [readyBtnActive, setReadyBtnActive] = useState(true);
    const [result, setResult] = useState(null);
    const [authHeaders, setAuthHeaders] = useState(props.authHeaders);

    useEffect(() => {
      if (websocket===null){
        setWebsocket(new WebSocket(`ws://${window.location.hostname}:8000/ws/${roomNumber}`))
      } 
      if (authHeaders != props.authHeaders) {
        setAuthHeaders(props.authHeaders)
      }
      if (gameStarted === true && countDown !== 0) {
        const timer = setInterval(() => {
          setCountDown((prevProgress) => (prevProgress == 0 ? 0 : prevProgress - 10));
        }, 1000);
        return () => {
          clearInterval(timer);
        };
      } else if (countDown == 0 && gameStarted === true) {
        onGameOver()
      }
    }, [websocket, countDown, gameStarted, roomNumber])


    function onGameOver() { 
      isGameStarted(false)
      isGameEnded(true)
      setReadyBtnActive(false)
      websocket.send(JSON.stringify({result: myChoice}))
      console.log(JSON.stringify({result: myChoice}))
    }

    
    if (websocket !== null) {
      websocket.onclose = function(event) {
        if (event.wasClean) {
          console.log(`[close] Соединение закрыто чисто, код=${event.code} причина=${event.reason}`);
        } else {
          // например, сервер убил процесс или сеть недоступна
          // обычно в этом случае event.code 1006
          console.log('[close] Соединение прервано', event);
        }
      };
  
      websocket.onopen = (event) => {
        console.log(event, "connect")
      }
  
      const addConnection = function(digit) {
        setConnections(digit)
      }
  
      websocket.onmessage = function(event) {
        let data = JSON.parse(event.data)
        if (data.connections) {
          addConnection(data.connections)
        }
        if (data.command) {
          let command = data.command
          if (command == "update_opponent_status") {
            setOpponentStatus(data.opponent_status)
          } if (command == "start") {
            isGameStarted(true)
          } if (command == "get_game_info") {
            websocket.send(
              JSON.stringify({
                "game_result": {
                  "room_number": roomNumber,
                  "user_info": currentUser.id,
                  "result": JSON.stringify(result),
                }
              })
            )
          }
        }
        if (data.result) {
          setResult(data.result)
          console.log(data.result)
          console.log(result)
        }
      }
    }

    function auth() {
      websocket.send(JSON.stringify({"auth": currentUser}))
    }

    function sendMyStatus(status) {
      if (websocket !== null) {
        websocket.send(JSON.stringify({"command": status}))
        setMyStatus(!myStatus)
      }
    }
    
    if (result == null) {
      return(
        <Flex
      minH={'100vh'}
      minW={'100vw'}
      align={'center'}
      justify={'center'}
      bg='gray.50'>
      <Container
        maxW={'lg'}
        bg={'white'}
        boxShadow={'xl'}
        rounded={'lg'}
        p={6}
        direction={'column'}>
        <Heading
          as={'h2'}
          fontSize={{ base: 'xl', sm: '2xl' }}
          textAlign={'center'}
          mb={3}>
          Привет, {currentUser.email} {currentUser.last_name}!
        </Heading>
        <Heading
          as={'h4'}
          fontSize={{ base: 'm', sm: 'lg' }}
          textAlign={'center'}
          mb={2}>
          room#{roomNumber}
        </Heading>
        <Heading
          as={'h4'}
          fontSize={{ base: 'm', sm: 'lg' }}
          textAlign={'center'}
          mb={2}>
          Connections: {connections}
        </Heading>
        <Stack
          direction={{ base: 'column', md: 'row' }}
          as={'form'}
          spacing={'12px'}
        >
          <div className="container d-flex flex-column justify-content-center align-items-center">
            <h1 className={gameStarted ? "fs-1" : "fs-1 d-none"}>
            <CircularProgress size='120px' value={countDown}>
              <CircularProgressLabel>{countDown/10}</CircularProgressLabel>
            </CircularProgress>
            </h1>
            <div className="row">
              <div className="input-group">
                <input 
                  onChange={(e)=>setMyChoice(e.currentTarget.id)} 
                  type="radio" 
                  className="btn-check" 
                  name="options" 
                  id="rock" 
                  autoComplete="off" 
                  defaultChecked disabled={gameStarted ? false : true}>
                </input>
                <label className="btn btn-primary" htmlFor="rock">
                  <img
                  src="static/js/E:/PythonProjects/FastAPI/fastapi_react_rcs/frontend/src/images/stone.png"
                  />
                </label>
        
                <input 
                  onChange={(e)=>setMyChoice(e.currentTarget.id)} 
                  type="radio" 
                  className="btn-check" 
                  name="options" 
                  id="scissors" 
                  autoComplete="off" 
                  disabled={gameStarted ? false : true}>
                </input>
                <label className="btn btn-primary" htmlFor="scissors">Ножницы</label>
        
                <input 
                  onChange={(e)=>setMyChoice(e.currentTarget.id)}  
                  type="radio" 
                  className="btn-check" 
                  name="options" 
                  id="paper" 
                  autoComplete="off" 
                  disabled={gameStarted ? false : true}>
                </input>
                <label className="btn btn-primary" htmlFor="paper">Бумага</label>
              </div>
            </div>
            <div className="row mt-2">
            <Stack direction='row'>
              <Button
                colorScheme={myStatus ? 'green' : 'red'}
                w="100%"
                id="myStatus"
                onClick={() => sendMyStatus(myStatus ? "not ready":"ready")} 
                isDisabled={gameStarted & readyBtnActive ? true : false}>
                {myStatus ? "ready":"not ready"}
              </Button>
            </Stack>
            </div>
            <div className="row mt-2">
              <span 
              className={opponentStatus ? 'col-12 badge badge-success' : "col-12 badge badge-danger"}>
                <small>Your opponent is </small><strong>{opponentStatus ? "ready":"not ready"}</strong>
              </span>
            </div>
          </div>
          </Stack>
      </Container>
    </Flex>
      )
    } else {
      return (
        <div className="container d-flex flex-column justify-content-center align-items-center">
          <ResultCard result={result}></ResultCard>
        </div>
      )
    }
  }
  
  export default GameRoom;
