// eslint-disable-next-line
import logo from '../images/logo.svg';
import '../styles/App.css';
import AuthForm from './AuthForm'
import SecondForm from './SecondForm'
import axios from 'axios'
import { useState } from 'react';

function App() {

  const [authStatus, setAuthStatus] = useState();
  const [currentUser, setCurrentUser] = useState();

  const getCookie = function (name) {
    if (!document.cookie) {
      return null;
    }

    const xsrfCookies = document.cookie.split(';')
      .map(c => c.trim())
      .filter(c => c.startsWith(name + '='));

    if (xsrfCookies.length === 0) {
      return null;
    }
    return decodeURIComponent(xsrfCookies[0].split('=')[1]);
  }

  const getToken = function() {
    console.log(getCookie("header_payload") + "." + getCookie("signature"))
    return getCookie("header_payload") + "." + getCookie("signature") 
  }

  const authParams = {
    headers: {
      "Authorization": `Bearer ${getToken()}`
    }
  }


  const getAuthStatus = function() {
    axios.get("http://localhost:8000/users/me/", authParams
    ).then(response => {return response.data})
  }


  const getUser = function() {
    axios.get("http://localhost:8000/users/me/", authParams
    ).then(response => {return response.status})
  }

  console.log(getUser())

  if (getUser() === 200) {
    <div>
      {getAuthStatus()}
    </div>
  } else {
    return (
      <AuthForm loggedIn={getUser}></AuthForm>
      );
  };
}

export default App;
