import { useEffect, useState } from "react";
import axios from 'axios'
import GameRoom from "./GameRoom"
import { useToast, Button } from '@chakra-ui/react';
import {
    Stack,
    FormControl,
    Input,
    useColorModeValue,
    Heading,
    Text,
    Container,
    Flex,
  } from '@chakra-ui/react';

function MainMenu(props) {

    const [roomNumber, setRoomNumber] = useState("");
    const [authHeaders, setAuthHeaders] = useState(props.authHeaders);
    const toast = useToast();


    useEffect(()=>{
        if (authHeaders !== props.authHeaders) {
            setAuthHeaders(props.authHeaders)
        }
    })

    const handleEnterClick = function() {
        axios.get(`http://localhost:8000/rooms/${roomNumber}`, {authHeaders}
        ).then(response => {
            if (response.status === 200) {
                props.enterToARoom(roomNumber)
            }
        }).catch(function(error) {
            console.log(error.response.status)
            console.log(error.response.data.detail)
        })
    }

    const createRoom = function() {
        axios.post("http://localhost:8000/rooms/", {
            authHeaders,
        }).then(response => {
            setRoomNumber(response.data.id);
        }).then(() =>
            toast({
              title: `Room has been created!`,
              variant: "top-accent",
              isClosable: true,
            })
        )
    }

    return(
        <Flex
        minH={'100vh'}
        align={'center'}
        justify={'center'}
        bg={useColorModeValue('gray.50', 'gray.800')}>
        <Container
          maxW={'lg'}
          bg={useColorModeValue('white', 'whiteAlpha.100')}
          boxShadow={'xl'}
          rounded={'lg'}
          p={6}
          direction={'column'}>
          <Heading
            as={'h2'}
            fontSize={{ base: 'xl', sm: '2xl' }}
            textAlign={'center'}
            mb={5}>
            Subscribe to our Newsletter
          </Heading>
          <Stack
            direction={{ base: 'column', md: 'row' }}
            as={'form'}
            spacing={'12px'}
            >
        <div className="container d-flex flex-column justify-content-center align-items-center">
        <div>–ü—Ä–∏–≤–µ—Ç, {props.currentUser.email}!</div>
        <div>room number is {roomNumber}</div>
        <div className="row">
            <div className="input-group">
                <input 
                    value={roomNumber}
                    onChange={(e)=>setRoomNumber(e.currentTarget.value)}
                    type="text" 
                    className="form-control" 
                    placeholder="–ù–æ–º–µ—Ä –∫–æ–º–Ω–∞—Ç—ã" 
                    aria-label="–ù–æ–º–µ—Ä –∫–æ–º–Ω–∞—Ç—ã" 
                    aria-describedby="button-addon2">    
                </input>
                <button className="btn btn-outline-primary" type="button" onClick={handleEnterClick}>–í–æ–π—Ç–∏</button>
            </div>
        </div>
        <span>or</span>
            <div className="row">
            <Button onClick={createRoom} colorScheme='teal' size='md'>
                Create a room
            </Button>
            </div>
        </div>
        </Stack>
        <Text
          mt={2}
          textAlign={'center'}
          color={error ? 'red.500' : 'gray.500'}>
          {error
            ? 'Oh no an error occured! üò¢ Please try again later.'
            : "You won't receive any spam! ‚úåÔ∏è"}
        </Text>
      </Container>
    </Flex>
    )
}



function Game(props) {


    const [currentUser, setCurrentUser] = useState(props.currentUser);
    const [currentRoomNumber, setCurrentRoomNumber] = useState(null);
    const [authHeaders, setAuthHeaders] = useState(null);

    
    useEffect(()=>{
        if (authHeaders != props.authHeaders) {
            setAuthHeaders(props.authHeaders)
        }
    })


    if (currentRoomNumber === null) {
        return (
            <MainMenu 
                authHeaders = {props.authHeaders}
                enterToARoom={(roomNumber) => setCurrentRoomNumber(roomNumber)} 
                roomNumberFromProps={currentRoomNumber} 
                currentUser={currentUser}>
            </MainMenu>
            
        )
    } else if (currentRoomNumber !== null) {
        console.log(currentRoomNumber)
        return (
            <GameRoom authHeaders={authHeaders} currentUser={currentUser} roomNumber={currentRoomNumber}></GameRoom>
        )
    }
  }
  
  export default Game;
