import React, { useState } from 'react';
import {
  MDBCard,
  MDBCardHeader,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardFooter,
  MDBBtn
} from 'mdb-react-ui-kit';

export default function ResultCard() {

    const [myItem, setMyItem] = useState();
    const [opponentItem, setOpponentItem] = useState();

    if (result.win == true) {
        setMyItem(result.winner_item)
        setOpponentItem(result.loser_item)
    } else if (result.win == false) {
        setMyItem(result.loser_item)
        setOpponentItem(result.winner_item)
    } else {
        setMyItem(result.item)
        setOpponentItem(result.item)
    }

    if (result.win != "no one") {
        return (
          <MDBCard alignment='center' shadow='0' border={props.result.win ? 'success' : 'danger'} background='white' className='mb-3'>
            <MDBCardHeader>Result</MDBCardHeader>
            <MDBCardBody>
              <MDBCardTitle>{props.result.win === "win" ? "Win!" : "Lose"}</MDBCardTitle>
              <MDBCardText>Your choice is <strong>{myItem}</strong></MDBCardText>
              <MDBCardText>Opponents choice is <strong>{opponentItem}</strong></MDBCardText>
            </MDBCardBody>
            <MDBCardFooter>
                <MDBBtn href='#'>Play again</MDBBtn>
            </MDBCardFooter>
          </MDBCard>
        );
    } else {
        return (
            <MDBCard alignment='center' shadow='0' border='primary' background='white' className='mb-3'>
              <MDBCardHeader>Result</MDBCardHeader>
              <MDBCardBody>
                <MDBCardTitle>{props.result.win === "win" ? "Win!" : "Lose"}</MDBCardTitle>
                <MDBCardText>Your choice is <strong>{myItem}</strong></MDBCardText>
                <MDBCardText>Opponents choice is <strong>{opponentItem}</strong></MDBCardText>
              </MDBCardBody>
              <MDBCardFooter>
                  <MDBBtn href='#'>Play again</MDBBtn>
              </MDBCardFooter>
            </MDBCard>
        );
    }
}
