import React, { useEffect, useState } from 'react';
import {
  MDBCard,
  MDBCardHeader,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardFooter,
  MDBBtn,

} from 'mdb-react-ui-kit';
import { Box, Heading, Text, Flex,   Button } from '@chakra-ui/react';
import { CheckCircleIcon, CloseIcon } from '@chakra-ui/icons';

export default function ResultCard(props) {

  const [myItem, setMyItem] = useState(null);
  const [opponentItem, setOpponentItem] = useState(null);


  useEffect(() => {
    if (myItem === null || opponentItem === null) {
      if (props.result.win == true) {
        setMyItem(props.result.winner_item)
        setOpponentItem(props.result.loser_item)
      } else if (props.result.win == false) {
        setMyItem(props.result.loser_item)
        setOpponentItem(props.result.winner_item)
      } else {
        setMyItem(props.result.item)
        setOpponentItem(props.result.item)
      }
    }
  });

  if (props.result.win != "no one") {
    return (
      <Box textAlign="center" py={10} px={6}>
        {props.result.win ? <CheckCircleIcon boxSize={'50px'} color={'green.500'} /> :
          <Box display="inline-block">
            <Flex
              flexDirection="column"
              justifyContent="center"
              alignItems="center"
              bg={'red.500'}
              rounded={'50px'}
              w={'55px'}
              h={'55px'}
              textAlign="center">
              <CloseIcon boxSize={'20px'} color={'white'} />
            </Flex>
          </Box>
        }
        <Heading as="h2" size="xl" mt={6} mb={2}>
          {props.result.win ? 'Congratulations!' : 'Maybe at a next time...'}
        </Heading>
        <Text color={'gray.500'}>
          <MDBCardText>Your choice is <strong>{myItem}</strong></MDBCardText>
          <MDBCardText>Opponents choice is <strong>{opponentItem}</strong></MDBCardText>
        </Text>
        <Button  onClick={() => window.location.reload()} colorScheme='teal' size='lg'>
          Play again
        </Button>
      </Box>
    );
  } else {
    return (
      <MDBCard alignment='center' shadow='0' border='primary' background='white' className='mb-3'>
        <MDBCardHeader>Result</MDBCardHeader>
        <MDBCardBody>
          <MDBCardTitle>Dead heat!</MDBCardTitle>
          <MDBCardText>You and your opponent have chosen <strong>{myItem}</strong></MDBCardText>
        </MDBCardBody>
        <MDBCardFooter>
          <MDBBtn onClick={() => window.location.reload()}>Play again</MDBBtn>
        </MDBCardFooter>
      </MDBCard>
    );
  }
}
