import React, { useEffect, useState } from 'react';
import {
  MDBCard,
  MDBCardHeader,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardFooter,
  MDBBtn
} from 'mdb-react-ui-kit';

export default function ResultCard(props) {

    const [myItem, setMyItem] = useState(null);
    const [opponentItem, setOpponentItem] = useState(null);

    useEffect(() => {
        if (myItem === null || opponentItem === null) {
            if (props.result.win == true) {
                setMyItem(props.result.winner_item)
                setOpponentItem(props.result.loser_item)
            } else if (props.result.win == false) {
                setMyItem(props.result.loser_item)
                setOpponentItem(props.result.winner_item)
            } else {
                setMyItem(props.result.item)
                setOpponentItem(props.result.item)
            }
        }
    });

    if (props.result.win != "no one") {
        return (
          <MDBCard alignment='center' shadow='0' border={props.result.win ? 'success' : 'danger'} background='white' className='mb-3'>
            <MDBCardHeader>Result</MDBCardHeader>
            <MDBCardBody>
              <MDBCardTitle>{props.result.win ? "Win!" : "Lose"}</MDBCardTitle>
              <MDBCardText>Your choice is <strong>{myItem}</strong></MDBCardText>
              <MDBCardText>Opponents choice is <strong>{opponentItem}</strong></MDBCardText>
            </MDBCardBody>
            <MDBCardFooter>
                <MDBBtn href='#'>Play again</MDBBtn>
            </MDBCardFooter>
          </MDBCard>
        );
    } else {
        return (
            <MDBCard alignment='center' shadow='0' border='primary' background='white' className='mb-3'>
              <MDBCardHeader>Result</MDBCardHeader>
              <MDBCardBody>
                <MDBCardTitle>{props.result.win === "win" ? "Win!" : "Lose"}</MDBCardTitle>
                <MDBCardText>Your choice is <strong>{myItem}</strong></MDBCardText>
                <MDBCardText>Opponents choice is <strong>{opponentItem}</strong></MDBCardText>
              </MDBCardBody>
              <MDBCardFooter>
                  <MDBBtn href='#'>Play again</MDBBtn>
              </MDBCardFooter>
            </MDBCard>
        );
    }
}
