import { useEffect, useState } from "react";
import axios from 'axios'
import GameRoom from "./GameRoom"

function MainMenu(props) {

    const [roomNumber, setRoomNumber] = useState("");
    const [authHeaders, setAuthHeaders] = useState(props.authHeaders);


    useEffect(()=>{
        if (authHeaders !== props.authHeaders) {
            setAuthHeaders(props.authHeaders)
        }
    })


    const handleEnterClick = function() {
        axios.get(`http://localhost:8000/rooms/${roomNumber}`
        ).then(response => {
            if (response.status === 200) {
                props.enterToARoom(roomNumber)
            }
        }).catch(function(error) {
            console.log(error.response.status)
            console.log(error.response.data.detail)
        })
    }

    const createRoom = function() {
        axios.post("http://localhost:8000/rooms/", {
            creator_id: props.currentUser.id
        }).then(response => console.log(response))
    }

    return(
        <div className="container d-flex flex-column justify-content-center align-items-center">
        <div>Привет, {props.currentUser.email}!</div>
        <div>room number is {roomNumber}</div>
        <div>room number from props is {props.roomNumberFromProps}</div>
        <div className="row">
            <div className="input-group">
                <input 
                    value={roomNumber}
                    onChange={(e)=>setRoomNumber(e.currentTarget.value)}
                    type="text" 
                    className="form-control" 
                    placeholder="Номер комнаты" 
                    aria-label="Номер комнаты" 
                    aria-describedby="button-addon2">    
                </input>
                <button className="btn btn-outline-primary" type="button" onClick={handleEnterClick}>Войти</button>
            </div>
        </div>
        <span>or</span>
            <div className="row">
                <button className="btn btn-outline-primary" type="button" id="button-addon2" onClick={createRoom}>Создать комнату</button>
            </div>
        </div>
    )
}



function Game(props) {


    const [currentUser, setCurrentUser] = useState(props.currentUser);
    const [currentRoomNumber, setCurrentRoomNumber] = useState(null);
    const [authHeaders, setAuthHeaders] = useState(null);

    if (currentRoomNumber === null) {
        return (
            <MainMenu 
                authHeaders = {props.authHeaders}
                enterToARoom={(roomNumber) => setCurrentRoomNumber(roomNumber)} 
                roomNumberFromProps={currentRoomNumber} 
                currentUser={currentUser}>
            </MainMenu>
        )
    } else if (currentRoomNumber !== null) {
        console.log(currentRoomNumber)
        return (
            <GameRoom authHeaders = {props.authHeaders} currentUser={currentUser} roomNumber={currentRoomNumber}></GameRoom>
        )
    }
  }
  
  export default Game;
