import { useEffect, useState, useRef, useDisclosure } from "react";
import axios from 'axios'
import GameRoom from "./GameRoom"
import {
    useToast, 
    Button,
    Stack,
    FormControl,
    Input,
    useColorModeValue,
    Heading,
    Container,
    Flex,
    Text,
    Divider,
    Drawer,
    DrawerBody,
    DrawerFooter,
    DrawerHeader,
    DrawerOverlay,
    DrawerContent,
    DrawerCloseButton,
} from '@chakra-ui/react';
import { ArrowForwardIcon } from '@chakra-ui/icons';

function MainMenu(props) {

    const { isOpen, onOpen, onClose } = useDisclosure()
    const btnRef = useRef()
    const [roomNumber, setRoomNumber] = useState("");
    const [authHeaders, setAuthHeaders] = useState(props.authHeaders);
    const [roomAvailable, setRoomAvailable] = useState(false)
    const [error, setError] = useState({ code: null, message: null })
    const toast = useToast();


    useEffect(() => {
        if (authHeaders !== props.authHeaders) {
            setAuthHeaders(props.authHeaders)
        }
    })

    const checkRoomAvailable = function (room) {
        if (room.length > 0) {
            setError({ code: null, message: null })
            axios.get(`http://localhost:8000/rooms/${room}`, { authHeaders }
            ).then(response => {
                console.log(response.status, "response status")
                if (response.status === 200) {
                    setRoomAvailable(true)
                } else if (response.status !== 200) {
                    setRoomAvailable(false)
                }
            }).catch(function (error) {
                setRoomAvailable(false)
                setError({ code: error.response.status, message: error.response.data.detail })
                console.log(error.response.status)
                console.log(error.response.data.detail)
            })
        }
    }

    const handleEnterClick = function () {
        if (roomNumber.length > 0) {
            axios.get(`http://localhost:8000/rooms/${roomNumber}`, { authHeaders }
            ).then(response => {
                if (response.status === 200) {
                    props.enterToARoom(roomNumber)
                }
            }).catch(function (error) {
                setError({ code: error.response.status, message: error.response.data.detail })
                console.log(error.response.status)
                console.log(error.response.data.detail)
            })
        }
    }

    const createRoom = function () {
        axios.post("http://localhost:8000/rooms/", {
            authHeaders,
        }).then(response => {
            setRoomNumber(response.data.id);
            setRoomAvailable(true)
        }).then(() =>
            toast({
                title: `Room has been created!`,
                variant: "top-accent",
                isClosable: true,
            })
        )
    }

    return (
        <Flex
            minH={'100vh'}
            minW={'100vw'}
            align={'center'}
            justify={'center'}
            bg={useColorModeValue('gray.50', 'gray.800')}>
            <Container
                maxW={'xl'}
                bg={useColorModeValue('white', 'whiteAlpha.100')}
                boxShadow={'xl'}
                rounded={'lg'}
                p={6}
                direction={'column'}>
                <Heading
                    as={'h2'}
                    fontSize={{ base: 'xl', sm: '2xl' }}
                    textAlign={'center'}
                    mb={2}>
                    Hi, {props.currentUser.email}!
                </Heading>
                <Heading
                    as={'h3'}
                    fontSize={{ base: 'lg', sm: 'xl' }}
                    textAlign={'center'}
                    mb={5}>
                    Enter to a room or create one
                </Heading>
                <Stack
                    direction={{ base: 'column', md: 'row' }}
                    as={'form'}
                    spacing={'12px'}>
                    <FormControl>
                        <Input
                            w="100%"
                            variant={'solid'}
                            borderWidth={1}
                            color={'gray.800'}
                            _placeholder={{
                                color: 'gray.400',
                            }}
                            borderColor={useColorModeValue('gray.300', 'gray.700')}
                            id={'room'}
                            type={'text'}
                            required
                            placeholder={'Room number'}
                            aria-label={'Room number'}
                            value={roomNumber}
                            onChange={(e) => { setRoomNumber(e.currentTarget.value); checkRoomAvailable(e.currentTarget.value) }}
                        />
                    </FormControl>
                    <FormControl w={{ base: '200%', md: '40%' }}>
                        <Button
                            colorScheme={roomAvailable && roomNumber.length > 0 ? 'green' : 'red'}
                            w="100%"
                            onClick={() => handleEnterClick()}
                            isDisabled={roomAvailable && roomNumber.length > 0 ? false : true}>
                            {roomAvailable && roomNumber.length > 0 ? <ArrowForwardIcon /> : 'Invalid'}
                        </Button>
                    </FormControl>
                </Stack>
                <Divider orientation='horizontal'/>
                <Stack direction={"row"} w={"100%"} justifyContent={"space-between"}>
                    <Button w={"100%"} onClick={createRoom} colorScheme='teal' size='md'>
                        Create a room
                    </Button>
                    <Button px={"10"} ref={btnRef} colorScheme='teal' onClick={onOpen} size='md'>
                        View my games
                    </Button>
                </Stack>
                <Text
                    mt={2}
                    textAlign={'center'}
                    color={error.code != null ? 'red.500' : 'gray.500'}>
                    {error.code !== null
                        ? `${error.code} üò¢ ${error.message}`
                        : "Good luck ‚úåÔ∏è"}
                </Text>
            </Container>
            <Drawer
                isOpen={isOpen}
                placement='right'
                onClose={onClose}
                finalFocusRef={btnRef}
            >
                <DrawerOverlay />
                <DrawerContent>
                    <DrawerCloseButton />
                    <DrawerHeader>Create your account</DrawerHeader>

                    <DrawerBody>
                        <Input placeholder='Type here...' />
                    </DrawerBody>

                    <DrawerFooter>
                        <Button variant='outline' mr={3} onClick={onClose}>
                            Cancel
                        </Button>
                        <Button colorScheme='blue'>Save</Button>
                    </DrawerFooter>
                </DrawerContent>
            </Drawer>
        </Flex>
        
    )
}



function Game(props) {


    const [currentUser, setCurrentUser] = useState(props.currentUser);
    const [currentRoomNumber, setCurrentRoomNumber] = useState(null);
    const [authHeaders, setAuthHeaders] = useState(null);


    useEffect(() => {
        if (authHeaders != props.authHeaders) {
            setAuthHeaders(props.authHeaders)
        }
    })


    if (currentRoomNumber === null) {
        return (
            <MainMenu
                authHeaders={props.authHeaders}
                enterToARoom={(roomNumber) => setCurrentRoomNumber(roomNumber)}
                roomNumberFromProps={currentRoomNumber}
                currentUser={currentUser}>
            </MainMenu>

        )
    } else if (currentRoomNumber !== null) {
        console.log(currentRoomNumber)
        return (
            <GameRoom authHeaders={authHeaders} currentUser={currentUser} roomNumber={currentRoomNumber}></GameRoom>
        )
    }
}

export default Game;
