import { useEffect, useState } from "react";
import { MDBRadio, MDBBtnGroup } from 'mdb-react-ui-kit';
import CircularProgress from '@mui/material/CircularProgress';

function GameRoom(props) {

    const [currentUser, setCurrentUser] = useState(props.currentUser);
    const [roomNumber, setRoomNumber] = useState(props.roomNumber);
    const [connections, setConnections] = useState(0);
    const [websocket, setWebsocket] = useState(null);
    const [myStatus, setMyStatus] = useState(false)
    const [opponentStatus, setOpponentStatus] = useState(false)
    const [gameStarted, isGameStarted] = useState(false)
    const [myChoice, setMyChoice] = useState("");
    const [countDown, setCountDown] = useState(0);

    useEffect(() => {
      if (websocket===null){
        setWebsocket(new WebSocket(`ws://${window.location.hostname}:8000/ws/${roomNumber}`))
      } 
      if (gameStarted === true && countDown !== 0) {
        const timer = setInterval(() => {
          setCountDown((prevProgress) => (prevProgress >= 100 ? 0 : prevProgress + 10));
        }, 800);
    
        return () => {
          clearInterval(timer);
        };
      }
    }, [])

    const timeout = setInterval(countDownFunction, 1000)
    
    if (websocket !== null) {
      websocket.onclose = function(event) {
        if (event.wasClean) {
          console.log(`[close] Соединение закрыто чисто, код=${event.code} причина=${event.reason}`);
        } else {
          // например, сервер убил процесс или сеть недоступна
          // обычно в этом случае event.code 1006
          console.log('[close] Соединение прервано', event);
        }
      };
  
      websocket.onopen = (event) => {
        console.log(event, "connect")
      }
  
      const addConnection = function(digit) {
        setConnections(digit)
      }
  
      websocket.onmessage = function(event) {
        let data = JSON.parse(event.data)
        if (data.connections) {
          addConnection(data.connections)
        }
        if (data.command) {
          let command = data.command
          if (command == "update_opponent_status") {
            setOpponentStatus(data.opponent_status)
          } if (command == "start") {
            isGameStarted(true)
          }
        }
      }
    }

    function countDownFunction() {
      if (countDown !== "Timer End") {
        if (countDown > 0) {
          setCountDown(countDown-1)
        } else {
          setCountDown("timer end")
          clearInterval(timeout)
        }
      }
    }

    function sendMyStatus(status) {
      if (websocket !== null) {
        websocket.send(JSON.stringify({"command": status}))
        setMyStatus(!myStatus)
      }
    }
    
    return(
        <div className="container d-flex flex-column justify-content-center align-items-center">
        <div>Привет, {currentUser.email} {currentUser.last_name}!</div>
        <div>room number is {roomNumber}</div>
        <div>Connections: {connections}</div>
        <h1 className={gameStarted ? "fs-1" : "fs-1 d-none"}>
          <CircularProgress variant="determinate" value={countDown} />  
        </h1>
        <div className="row">
          <div className="input-group">
            <input 
              onChange={(e)=>setMyChoice(e.currentTarget.id)} 
              type="radio" 
              className="btn-check" 
              name="options" 
              id="rock" 
              autoComplete="off" 
              defaultChecked disabled={gameStarted ? false : true}>
            </input>
            <label className="btn btn-primary" htmlFor="rock">Камень</label>

            <input 
              onChange={(e)=>setMyChoice(e.currentTarget.id)} 
              type="radio" 
              className="btn-check" 
              name="options" 
              id="scissors" 
              autoComplete="off" 
              disabled={gameStarted ? false : true}>
            </input>
            <label className="btn btn-primary" htmlFor="scissors">Ножницы</label>

            <input 
              onChange={(e)=>setMyChoice(e.currentTarget.id)}  
              type="radio" 
              className="btn-check" 
              name="options" 
              id="paper" 
              autoComplete="off" 
              disabled={gameStarted ? false : true}>
            </input>
            <label className="btn btn-primary" htmlFor="paper">Бумага</label>
          </div>
        </div>
          <div className="row mt-2">
            <button 
            className={myStatus ? "btn btn-outline-success col-12" : "btn btn-outline-danger col-12" }
            onClick={() => sendMyStatus(myStatus ? "not ready":"ready")}  
            type="button" 
            id="myStatus"
            disabled={gameStarted ? true : false}>
              {myStatus ? "ready":"not ready"}
            </button>
          </div>
          <div className="row mt-2">
            <span 
            className={opponentStatus ? 'col-12 badge badge-success' : "col-12 badge badge-danger"}>
              <small>Your opponent is </small><strong>{opponentStatus ? "ready":"not ready"}</strong>
            </span>
          </div>
        </div>
    )
  }
  
  export default GameRoom;
